mapper.select:
UPDATE
	matnamMember
SET
	mnMmEmail = #{mnMmEmail}
	,mnMmIntroduce = #{mnMmIntroduce}
WHERE 1=1
	AND mnMmSeq = #{mnMmSeq}

UPDATE
	matnamMemberPhone
SET
	mnmpNumber = #{mnmpNumber}
WHERE 1=1
	AND mnMmSeq = #{mnMmSeq}

UPDATE
	matnamMemberAddress
SET
	mnmaAddress1 = #{mnmaAddress1}
WHERE 1=1
	AND mnMmSeq = #{mnMmSeq}

mapper.insert:
INSERT INTO matnamMember(
	mnMmId
	,mnMmPassword
	,mnMmName
	,mnMmDob_Year
	,mnMmDob_Month_Cd
	,mnMmDob_Day
	,mnMmAdminNy
	,mnMmGenderCd
	,mnMmDelNy
	,regDateTime
	,modDateTime
) VALUES (
	#{mnMmId}
	,#{mnMmPassword}
	,#{mnMmName}
	,#{mnMmDob_Year}
	,#{mnMmDob_Month_Cd}
	,#{mnMmDob_Day}
	,0
	,#{mnMmGenderCd}
	,0
	,#{regDateTime}
	,#{modDateTime}
)

	SELECT last_insert_id()
