mapper.update:
			UPDATE matnam.matnamReview SET 
				mnrvText  = #{mnrvText}
			WHERE 1=1
				AND mnrtSeq = #{mnrtSeq}
                AND mnMmSeq = #{mnMmSeq};

mapper.delete:
		DELETE 
    	FROM matnam.matnamReview
		WHERE 1=1
			AND mnrtSeq = #{mnrtSeq}
			AND mnMmSeq = #{mnMmSeq}
			AND mnrvSeq = #{mnrvSeq};
