mapper.select:
SELECT mnevSeq , mnevTitle , mnevStartDate , mnevEndDate , mnevContent , mnevUseNy FROM matnamEvent WHERE 1=1
And mnevUseNy = 1 And mnevUseNy = 0
AND mnevTitle LIKE concat('%',#{shMnevTitle},'%') AND mnevContent LIKE concat('%',#{shMnevContent},'%') limit #{startRnumForMysql}, #{rowNumToShow}

mapper.insert:
INSERT INTO matnamEvent (
	mnevTitle
	, mnevContent
	, mnevDelNy
) VALUES (
	#{mnevTitle}
	, #{mnevContent}
	, 0
)

mapper.update:
UPDATE matnamEvent SET
	mnevTitle = #{mnevTitle}
	, mnevContent = #{mnevContent}
WHERE 1=1
	and mnevSeq = #{mnevSeq}
